INSERT INTO `faqs` (`id`, `language_id`, `question`, `answer`, `serial_number`) VALUES
(20, 176, '1. Quão fácil é configurar minha loja online?', 'Configurar sua loja é rápido e simples. Nossa plataforma oferece um guia passo a passo, e você pode ter sua loja no ar em poucos minutos. Nenhuma habilidade técnica é necessária!', 1),
(23, 176, '2. Posso personalizar o design da minha loja?', 'Com certeza! Você pode escolher entre vários templates e personalizá-los para combinar com a identidade da sua marca. Com ferramentas de design fáceis de usar, você pode criar uma loja que se destaque.', 2),
(24, 176, '3. Meus dados estão seguros na sua plataforma?', 'Sim, seus dados e transações estão totalmente seguros. Usamos criptografia de alto nível e seguimos as melhores práticas do setor para garantir a segurança do seu negócio e dos dados dos clientes.', 3),
(25, 176, '4. Quais métodos de pagamento posso oferecer aos meus clientes?', 'Nossa plataforma suporta uma ampla variedade de gateways de pagamento, incluindo cartões de crédito, PayPal, Stripe e muitos outros, oferecendo opções flexíveis para seus clientes.', 4),
(26, 176, '5. Posso acompanhar minhas vendas e desempenho?', 'Sim, com nossas ferramentas de análise integradas, você pode acompanhar as vendas, monitorar o comportamento dos clientes e obter insights para ajudar a crescer seu negócio. Relatórios em tempo real ajudam você a se manter informado o tempo todo.', 5),
(27, 176, '6. Vocês oferecem suporte caso eu precise de ajuda?', 'Com certeza! Nossa equipe de suporte está disponível 24/7 para ajudar com qualquer dúvida ou problema. Também temos um centro de ajuda completo com tutoriais e guias.', 6),
(28, 176, '7. Posso expandir minha loja à medida que meu negócio cresce?', 'Sim! Nossa plataforma foi projetada para crescer com seu negócio. Você pode adicionar facilmente mais produtos, usuários e recursos conforme sua empresa se expande, sem se preocupar com limitações.', 7);
INSERT INTO `features` (`id`, `language_id`, `icon`, `title`, `text`, `serial_number`) VALUES
(69, 176, '7b6c8da163bb39e6604111f74b86b713ac6da569.png', 'Suporte ao Cliente 24/7', 'Receba suporte a qualquer hora do dia ou da noite. Nossa equipe está pronta para ajudar você com qualquer problema ou dúvida.', 4),
(70, 176, '2962e25458b19da6b18d2e1c7e14310e360c65ac.png', 'Escalável para Crescimento', 'Nossa plataforma cresce junto com seu negócio, oferecendo a flexibilidade para adicionar novos produtos, recursos e usuários facilmente.', 3),
(71, 176, '8846a7431e82d3321833a9bda3d7ce5659fae7ee.png', 'Pagamentos Seguros', 'Garanta que os dados dos seus clientes estejam protegidos com segurança de alto nível para um processamento de pagamento confiável e tranquilo.', 2),
(72, 176, '1016af06183bc1d29994fd075b5df8c5fb9df598.png', 'Painel Fácil de Usar', 'Gerencie seu negócio sem esforço com um painel intuitivo que oferece controle total sobre produtos, pedidos e análises em um só lugar.', 1);
