INSERT IGNORE INTO `faqs` VALUES (60, 176, '\'١. ما مدى سهولة إنشاء متجري عبر الإنترنت؟\'', '\'إعداد متجرك سريع وبسيط. توفر منصتنا إرشادات خطوة بخطوة ، ويمكنك تشغيل متجرك في غضون دقائق. لا المهارات التقنية المطلوبة!\'', 1);
INSERT IGNORE INTO `faqs` VALUES (61, 176, '\'٢. هل يمكنني تخصيص تصميم متجري؟\'', '\'متنوعة من القوالب وتخصيصها لتتناسب مع هوية علامتك التجارية. باستخدام أدوات التصميم سهلة الاستخدام ، يمكنك إنشاء متجر متميز.\'', 2);
INSERT IGNORE INTO `faqs` VALUES (62, 176, '\'٣. هل بياناتي آمنة على النظام الأساسي الخاص بك؟\'', '\'نعم، بياناتك ومعاملاتك آمنة تماما. نحن نستخدم تشفيرا عالي المستوى ونتبع أفضل ممارسات الصناعة لضمان سلامة عملك وبيانات العملاء.\'', 3);
INSERT IGNORE INTO `faqs` VALUES (63, 176, '\'٤. ما هي طرق الدفع التي يمكنني تقديمها لعملائي؟\'', '\'تدعم منصتنا مجموعة واسعة من بوابات الدفع بما في ذلك بطاقات الائتمان و PayPal و Stripe وغيرها الكثير ، مما يمنح عملائك خيارات دفع مرنة.\'', 4);
INSERT IGNORE INTO `faqs` VALUES (64, 176, '\'٥. هل يمكنني تتبع مبيعاتي وأدائي؟\'', '\'نعم ، باستخدام أدوات التحليلات المضمنة لدينا ، يمكنك تتبع المبيعات ومراقبة سلوك العملاء واكتساب رؤى للمساعدة في تنمية أعمالك. تساعدك التقارير في الوقت الفعلي على البقاء على اطلاع في جميع الأوقات.\'', 5);
INSERT IGNORE INTO `faqs` VALUES (65, 176, '\'٦. هل تقدمون الدعم إذا كنت بحاجة إلى مساعدة؟\'', '\'التاكيد! فريق الدعم لدينا متاح 24/7 لمساعدتك في أي أسئلة أو مشاكل. لدينا أيضا مركز مساعدة شامل مع البرامج التعليمية والأدلة.\'', 6);
INSERT IGNORE INTO `faqs` VALUES (66, 176, '\'٧. هل يمكنني توسيع نطاق متجري مع نمو عملي؟\'', '\'نعم! تم تصميم منصتنا لتنمو مع عملك. يمكنك بسهولة إضافة المزيد من المنتجات والمستخدمين والميزات مع توسع عملك دون القلق بشأن القيود.\'', 7);
INSERT IGNORE INTO `features` VALUES (76, 176, '\'1c612f7b4db568d4f0997ac0e4902cfc9574b35c.png\'', '\'لوحة تحكم سهلة الاستخدام\'', '\'قم بإدارة أعمالك بسهولة باستخدام لوحة تحكم بديهية تمنحك التحكم الكامل في المنتجات والطلبات والتحليلات في مكان واحد.\'', 1);
INSERT IGNORE INTO `features` VALUES (77, 176, '\'6385443a4d26697b121cf8f8595f5be1ba5fca8a.png\'', '\'المدفوعات الآمنة\'', '\'تأكد من حماية بيانات عملائك بأمان عالي المستوى من أجل معالجة دفع سلسة وموثوقة.\'', 2);
INSERT IGNORE INTO `features` VALUES (78, 176, '\'6b159881a3004dff738959383cf3d5215750f484.png\'', '\'قابلة للنمو\'', '\'تنمو منصتنا مع عملك، مما يوفر المرونة اللازمة لإضافة منتجات وميزات ومستخدمين جدد بسهولة\'', 3);
INSERT IGNORE INTO `features` VALUES (79, 176, '\'3788cf469d564fcb72a0516c84222e751c75de3d.png\'', '\'دعم العملاء\'', '\'احصل على الدعم على مدار الساعة عندما تحتاج إليه. فريقنا هنا لمساعدتك في أي مشاكل أو أسئلة.\'', 4);
