INSERT IGNORE INTO `testimonials` VALUES (47, 176, 'NULL', '\'41f019f44e1cd7e5ba000c82ac915e2227e01d35.png\'', '\'منذ التحول إلى هذه المنصة ، زادت مبيعاتنا بنسبة 30٪! لقد منحنا التكامل مع بوابات الدفع وأدوات التحليل الرؤى التي نحتاجها للنمو.\'', '\'مايكل ت.\'', '\'رائد أعمال في التجارة الإلكترونية\'', 2, 'NULL', 'NULL');
INSERT IGNORE INTO `testimonials` VALUES (48, 176, 'NULL', '\'6c6596f9c27562fe003a41e28b840ef601be4d24.png\'', '\'أحب مدى قابلية النظام الأساسي للتطوير. مع نمو عملي ، لم يكن لدي ما يدعو للقلق بشأن ترقية الأنظمة أو فقدان السيطرة. كل شيء يعمل فقط ، وفريق الدعم رائع!\'', '\'جيسيكا ر.\'', '\'بائع تجزئة للأزياء\'', 3, 'NULL', 'NULL');
INSERT IGNORE INTO `testimonials` VALUES (49, 176, 'NULL', '\'fb117c1fa7f4b6e265779a96ce82533b8fbcc53a.png\'', '\'الأمان وسهولة الاستخدام من الدرجة الأولى. يثق عملائي في عملية الدفع ، ويمكنني بسهولة إدارة الطلبات والمخزون دون أي متاعب.\'', '\'ديفيد س.\'', '\'بائع المنتجات التقنية\'', 4, 'NULL', 'NULL');
INSERT IGNORE INTO `testimonials` VALUES (50, 176, 'NULL', '\'13fd6b3eeaa68a3cea83870f79ed4bfa83cd9bf6.png\'', '\'لقد غيرت هذه المنصة قواعد اللعبة لعملي. إنه سهل الإعداد ، والميزات هي بالضبط ما أحتاجه لإدارة مخزوني وطلباتي. أفضل جزء هو مدى موثوقيتها!\'', '\'إميلي و.\'', '\'مالك العلامة التجارية للصحة والعافية\'', 5, 'NULL', 'NULL');
INSERT IGNORE INTO `testimonials` VALUES (51, 176, 'NULL', '\'69fe0678a4e5867a292334ce7dc3cc61899f0540.png\'', '\'لقد ساعدتني أدوات التحليلات وإعداد التقارير في اتخاذ قرارات أفضل لأعمال عملائي. من السهل تتبع الأداء وتحسين الاستراتيجيات. لا أستطيع أن أتخيل إدارة عملي بدون هذه المنصة\'', '\'مرسيدس جنسن\'', '\'أخصائي تسويق رقمي\'', 6, 'NULL', 'NULL');
